import React, {Component} from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import logo from '../images/logo.png';
import '../css/KM.css'
import Image from "react-bootstrap/Image";
import ReactCardFlip from "react-card-flip";

const KM = React.memo(() => {
    return(
        <Container className="wide">
            <Row>
                <Col sm>
                    <div className={"person-card-wrapper"}>
                        <Image className={"portrait"} src={logo} />
                        <div>
                            <h1>Oliver Jensen</h1>
                        </div>
                    </div>
                </Col>
                <Col sm>
                    <div className={"person-card-wrapper"}>
                        <Image className={"portrait"} src={logo} />
                    </div>
                </Col>
                <Col sm>
                    <div className={"person-card-wrapper"}>
                        <Image className={"portrait"} src={logo} />
                    </div>
                </Col>
            </Row>
            <Row>
                <Col sm>
                    <div className={"person-card-wrapper"}>
                        <Image className={"portrait"} src={logo} />
                    </div>
                </Col>
                <Col sm>
                    <div className={"person-card-wrapper"}>
                        <Image className={"portrait"} src={logo} />
                    </div>
                </Col>
                <Col sm>

                </Col>
            </Row>
        </Container>
    )
});

export default KM;

class PresentationCard extends Component {
    constructor (props) {
        super (props);
        this.state = { isFlipped: false };
    }

    render(){
        return (
            <div className={"person-card-wrapper"} onClick={() => this.setState ({ isFlipped: !this.state.isFlipped })}>
                <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="horizontal">
                    <img src={logo} className="home-shield" alt="logo" key="front" />
                    <img src={logo} className="home-shield" alt="logo" key="back" />
                </ReactCardFlip>
            </div>
        )
    }
}
