import React, {Component} from 'react';
import './css/App.css';
import bg from "./bg.jpg";
import Home from "./content/Home";
import Navbar from "./components/Navbar";
import Events from "./content/Events";

const page_content = {
    "home":    <Home/>,
    "events":  <Events/>,
    "km":      <Home/>,
    "contact": <Home/>,
    "login":   <Home/>
};

const default_page_content = { id: "home", component: <Home/> };

class App extends Component {

    constructor(props) {
        super(props);
        this.state = {};
    }

    componentDidMount() {
        this.setState({current_page: default_page_content.id});
    }

    change_current_page(id){
        console.log("Before", this.state.current_page);
        this.setState({ current_page: id });
        console.log("After", this.state.current_page);
    }

    render() {
        const content = (this.state.current_page in page_content)
            ? page_content[this.state.current_page]
            : default_page_content.component;

        return (
            <div className="App">
                <img src={bg} id={"bg"} alt={"Background"}/>
                <Navbar onSelect={(id) => this.change_current_page(id)}/>
                { content }
            </div>
        )
    }
}

export default App;
