import React, {Component} from 'react';
import './css/App.css';
import bg from "./bg.jpg";
import Home from "./content/Home";
import Navbar from "./components/Navbar";
import Schedule from "./content/Schedule";
import Login from "./content/Login";

const pages = {
    HOME: "home",
    SCHEDULE: "schedule",
    KM: "km",
    CONTACT: "contact",
    LOGIN: "login"
};

const page_content = {
    page:    <Home/>,
    "schedule":  <Schedule/>,
    "km":      <Home/>,
    "contact": <Home/>,
    "login":   <Login/>
};

const default_page_content = { id: "home", component: <Home/> };

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { page: props.page };
    }

    componentDidMount() {
        if (this.state.page === undefined)
            this.setState({page: default_page_content.id});
    }

    render() {
        const content = (this.state.page in page_content)
            ? page_content[this.state.page]
            : default_page_content.component;

        return (
            <div className="App">
                <img src={bg} id={"bg"} alt={"Background"}/>
                <Navbar onSelect={(id) => this.setState({ page: id })}/>
                { content }
            </div>
        )
    }
}

export default App;
