import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import logo from '../images/logo.png';
import '../css/KM.css'
import Card from 'react-bootstrap/Card';

const info = [
    {
        titel: "Klubbmästare",
        namn: "Oliver Jensen",
        img: null
    },
    {
        titel: "Vice Klubbmästare",
        namn: "Johan Widmark",
        img: null
    },
    {
        titel: "Pubmästare",
        namn: "Adam Henriksson",
        img: null
    },
    {
        titel: "Marsall",
        namn: "",
        img: null
    },
    {
        titel: "Marsall",
        namn: "",
        img: null
    }
];



const KM = React.memo(() => {

    let contentLength = 0;
    const content = info.forEach((person, i) => {
        contentLength = i;
        return (
            <Col sm key={"col_"+i}>
                <Card bg="dark">
                    <Card.Header>{ person.title }</Card.Header>
                    <Card.Body>
                        <Card.Img src={logo}/>
                        <Card.Title>{ person.name }</Card.Title>
                    </Card.Body>
                </Card>
            </Col>
        )
    });

    console.log("Length", contentLength);

    let rowsList = [];
    for (let i = 0; i*3 > contentLength; i++) {
        console.log("slice");
        rowsList[i] = content.slice(i*3, (i+1)*3)
    }

    let rowListCount = 0;

    const rowContent = rowsList.map((row, i) => {
        rowListCount = i;
        return (
            <Row key={"row_"+i}>
                { row }
            </Row>
        )
    });

    console.log("RowLength", rowListCount);

    return(
        <Container className="wide">
            { rowContent }
        </Container>
    )
});


export default KM;
