import React, {Component} from "react";
import logo from "../images/logo.png";
import '../css/Home.css';
import Container from "react-bootstrap/Container";
import Flipper from "../components/react-flipper/src/components/Flipper";
import Front from "../components/react-flipper/src/components/Front";
import Back from "../components/react-flipper/src/components/Back";


class Home extends Component {
    constructor (props) {
        super (props);
        this.state = { isFlipped: false };
    }

    flip = () => {
        this.setState ({ isFlipped: !this.state.isFlipped });
    }

    render(){
        return (
            <Container>
                <Flipper isFlipped={this.state.isFlipped} orientation="horizontal">
                    <Front>
                        <img src={logo} className="home-shield" alt="logo"/>
                    </Front>
                    <Back>
                        <img src={logo} className="home-shield" alt="logo"/>
                    </Back>
                </Flipper>
            </Container>
        )
    }
}

export default Home
